import React, { Fragment, useEffect, useState } from "react";
import { getProduct } from "../../services/products";
import { Button, Card, CardContent, Typography } from '@mui/material';
const Product = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetchData();
    return () => null;
    // eslint-disable-next-line
  }, []);

let product_id
let price
let category
let availability_status

  const fetchData = async () => {
    const data = await getProduct({
      product_id,
      price,
      category,
      availability_status,
    });

    setProducts(data?.message?.items_list);
    console.log("XXXXXXXXXXXXXXXXXx",data);
    
  };
  console.log("products", products);

  return (
    <Fragment>
        {/* <Card> */}
        {/* <CardContent> */}
        {products?.length === 0 ? (
            <div>
                no result
            </div>
        )
        :
        (
            products.map((item)=>
                <Card key={item.product_id} style={{
                width: "300px",  // Set a fixed width for each card
                flexGrow: 1000,
                margin: "10px"      // Allow cards to grow within the flex container
              }}>
            <CardContent>
              <Typography variant="h5" component="div">{item.name}</Typography>
              <Typography variant="body2">{item.category}</Typography>
              <Typography variant="body2">{item.description}</Typography>
              <Typography variant="body2">{item.price}</Typography>
              <Typography variant="body2">{item.availability_status}</Typography>
            </CardContent>
          </Card>

            )
        )}
        {/* </CardContent> */}
        {/* </Card> */}
        <Button variant="contained" color="primary">Add Product</Button>
    </Fragment>
  );
};

export default Product;
