import React, { Fragment, useEffect, useState } from "react";
import { getProduct, deleteProduct } from "../../services/products";
import {
  Button,
  Card,
  CardContent,
  Typography,
  Box,
  MenuItem,
  Select,
  Divider,
  FormControl,
  InputLabel,
  TextField
} from "@mui/material";
import { ToastContainer, toast } from "react-toastify";
import Snackbar from "@mui/material/Snackbar";

import ModalWrapper from "../../components/ModalWrapper";
import DeleteModal from "../../components/deleteModal";

import "../../styles/styles.css";
import { async } from "q";

const Product = () => {
  const [products, setProducts] = useState([]);
  const [isOpen, setIsOpen] = useState(false);
  const [selectedRow, setSelectedRow] = useState();
  const [deleteModal, setDeleteModal] = useState(false);
  const [deleteId, setDeleteId] = useState();
  const [filter, setFilter] = useState();
  const [productId, setproductId] = useState();
  const [price, setPrice] = useState();
  const [category, setCategory] = useState();
  const [availabilityStatus, setSvailabilityStatus] = useState();

  const [isSnackbarOpen, setSnackbarOpen] = useState(false);

  const filters = ["All", "category", "price", "availability"];

  useEffect(() => {
    fetchData();
    return () => null;
    // eslint-disable-next-line
  }, []);

  const fetchData = async () => {
    const data = await getProduct({
      product_id: productId,
      price,
      category,
      availability_status: availabilityStatus,
    });

    setProducts(data?.message?.items_list);
  };

  const handleToggle = () => {
    setIsOpen(!isOpen);
  };

  const addProduct = () => {
    setSelectedRow(null);
    handleToggle();
  };

  const handleClick = async (item) => {
    const response = await getProduct({ product_id: item.product_id });

    setSelectedRow(response?.message?.items_list[0]);
    handleToggle();
  };

  const updateProduct = (editProduct) => {
    const _products = [...products];
    const _newProducts = _products.filter(
      (item) => item.product_id !== editProduct.product_id
    );
    _newProducts.unshift({ ...editProduct });
    setProducts(_newProducts);
  };

  const handleDeleteProduct = async () => {
    const response = await deleteProduct(deleteId);

    if (response && response.status) {
      const data = [...products];
      const _data = data.filter((item) => item.product_id !== deleteId);
      setProducts(_data);
      //   toast.success("Successfully Deleted");
    }
  };

  const handleClose = () => {
    setSnackbarOpen(false);
  };

  const handleFilter = async (value) => {
    setFilter(value);
    let response  
    if (value === "praice") {
       response = await getProduct({ price: value });
    } else if (value === "category") {
       response = await getProduct({ category: value });
    } else if (value === "availability") {
       response = await getProduct({ availability_status: value });
    }else{
        fetchData()

    }
    setProducts(response?.message?.items_list);
  };
  return (
    <Box flex={1} p={4}>
      {/* <Card> */}
      {/* <CardContent> */}
      <Snackbar
        anchorOrigin={{ vertical: "top", horizontal: "center" }}
        open={isSnackbarOpen}
        onClose={handleClose}
        autoHideDuration={5000}
        message="I love snacks"
        key={"top" + "center"}
      />
      <Box
        display="flex"
        justifyContent="space-around"
        alignItems={"center"}
        mb={4}
      >
        <h3>Task development</h3>

        <FormControl>
          <InputLabel id="demo-simple-select-label">Filter</InputLabel>
          <Select
            className="select-width"
            label="Filter"
            labelId="demo-simple-select-label"
            variant="outlined"
            value={filter}
            onChange={(e) => {
              handleFilter(e.target.value);
            }}
          >
            {filters.map((item, index) => (
              <MenuItem key={`product-status-${index}`} value={item}>
                {item}
              </MenuItem>
            ))}
          </Select>
        </FormControl>

        <FormControl>
          <InputLabel id="demo-simple-select-label">Filter</InputLabel>
          <TextField
              variant="outlined"
              id="price"
              placeholder="I to filter"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              // helperText={!price && validationErrors.price}
            />
        </FormControl>

        <Button
          variant="contained"
          color="primary"
          onClick={addProduct}
          className="mb-3 mt-3 align-self-center"
        >
          Add Product
        </Button>
      </Box>
      <Divider orientation="horizontal" flexItem className="mb-3">
        Products
      </Divider>
      {products?.length === 0 ? (
        <div>no result</div>
      ) : (
        products.map((item) => (
          <Card
            key={item.product_id}
            style={{
              width: "100%",
              flexGrow: 1,
              marginBottom: 30,
            }}
          >
            <CardContent>
              <Typography variant="h5" component="div" mb={3}>
                {item.name}
              </Typography>
              <Box flex={1} mb={3}>
                <Typography mb={1} variant="body2">
                  Category: {item.category}
                </Typography>
                <Typography mb={1} variant="body2">
                  Description: {item.description}
                </Typography>
                <Typography mb={1} variant="body2">
                  Price: {item.price}
                </Typography>
                <Typography mb={1} variant="body2">
                  Availability: {item.availability_status}
                </Typography>
              </Box>
              <Box
                display="flex"
                flex={1}
                width="100%"
                justifyContent={"space-between"}
              >
                <Button
                  variant="contained"
                  color="primary"
                  onClick={() => handleClick(item)}
                >
                  Update Product
                </Button>

                <Button
                  variant="contained"
                  color="red"
                  onClick={() => {
                    setDeleteModal(true);

                    setDeleteId(item.product_id);
                  }}
                >
                  Delete Product
                </Button>
              </Box>
            </CardContent>
          </Card>
        ))
      )}
      {isOpen && (
        <ModalWrapper
          isOpen={isOpen}
          toggle={handleToggle}
          setSnackbarOpen={setSnackbarOpen}
          products={products}
          setProducts={setProducts}
          selectedRow={selectedRow}
          updateListingData={updateProduct}
        />
      )}

      {deleteModal && (
        <DeleteModal
          isOpen={deleteModal}
          toggle={() => setDeleteModal(false)}
          onDelete={handleDeleteProduct}
        />
      )}
    </Box>
  );
};

export default Product;
