import React, { Fragment, useEffect, useState } from "react";
import { getProduct, deleteProduct } from "../../services/products";
import { Button, Card, CardContent, Typography } from "@mui/material";

import ModalWrapper from "../../components/ModalWrapper";
import DeleteModal from "../../components/deleteModal";

const Product = () => {
  const [products, setProducts] = useState([]);
  const [isOpen, setIsOpen] = useState(false);
  const [selectedRow, setSelectedRow] = useState();
  const [deleteModal, setDeleteModal] = useState(false);
  const [deleteId, setDeleteId] = useState();

  useEffect(() => {
    fetchData();
    return () => null;
    // eslint-disable-next-line
  }, []);

  let product_id;
  let price;
  let category;
  let availability_status;

  const fetchData = async () => {
    const data = await getProduct({
      product_id,
      price,
      category,
      availability_status,
    });

    setProducts(data?.message?.items_list);
    console.log("XXXXXXXXXXXXXXXXXx", data);
  };
  console.log("products", products);

  const handleToggle = () => {
    setIsOpen(!isOpen);
  };

  const addProduct = () => {
    setSelectedRow(null);
    handleToggle();
  };

  const handleClick = async (item) => {
    const response = await getProduct({ product_id: item.product_id });

    setSelectedRow(response?.message?.items_list[0]);
    handleToggle();
  };

  const updateProduct = (editProduct) => {
    const _products = [...products];
    const _newProducts = _products.filter(
      (item) => item.product_id !== editProduct.product_id
    );
    _newProducts.unshift({ ...editProduct });
    setProducts(_newProducts);
  };



  const handleDeleteProduct = async () => {
    setDeleteModal(true)
    // setDeleteModal(true)
    const response = await deleteProduct(deleteId);

    if (response && response.status) {
      const data = [...products];
      const _data = data.filter((item) => item.product_id !== deleteId);
      setProducts(_data);
      toast.success("Successfully Deleted");
    }
  };


  return (
    <Fragment>
      {/* <Card> */}
      {/* <CardContent> */}
      <Button
        variant="contained"
        color="primary"
        onClick={addProduct}
        className="mb-3 mt-3 align-self-center"
      >
        Add Product
      </Button>
      {products?.length === 0 ? (
        <div>no result</div>
      ) : (
        products.map((item) => (
          <Card
            key={item.product_id}
            style={{
              width: "600px", // Set a fixed width for each card
              flexGrow: 1,
              margin: "10px", // Allow cards to grow within the flex container
            }}
          >
            <CardContent>
              <Typography variant="h5" component="div">
                {item.name}
              </Typography>
              <Typography variant="body2">{item.category}</Typography>
              <Typography variant="body2">{item.description}</Typography>
              <Typography variant="body2">{item.price}</Typography>
              <Typography variant="body2">
                {item.availability_status}
              </Typography>
              <Button
                variant="contained"
                color="primary"
                onClick={() => handleClick(item)}
              >
                Update Product
              </Button>


              <Button variant="contained" color="primary"
              onClick={() => setDeleteModal(true)}
              >
                Delete Product
              </Button>
            </CardContent>
          </Card>
        ))
      )}
      {/* </CardContent> */}
      {/* </Card> */}
      {isOpen && (
        <ModalWrapper
          isOpen={isOpen}
          toggle={handleToggle}
          products={products}
          setProducts={setProducts}
          selectedRow={selectedRow}
          updateListingData={updateProduct}
        />
      )}

      {deleteModal && (
        <DeleteModal
          isOpen={deleteModal}
          toggle={() => setDeleteModal(false)}
          onDelete={handleDeleteProduct}
        />
      )}
    </Fragment>
  );
};

export default Product;
