import React, {useState } from "react";
import { createProduct, updateProduct } from "../services/products";
import { Button, Dialog, DialogTitle, DialogContent, DialogActions, Box, TextField, Typography, MenuItem, Select, FormControl, FormHelperText } from "@mui/material";
const ModalWrapper = ({
  isOpen,
  toggle,
  products,
  setProducts,
  selectedRow,
  // updateListingData,
}) => {
  const [name, setName] = useState(selectedRow?.name || "");
  const [price, setPrice] = useState(selectedRow?.price || "active");
  const [category, setCategory] = useState(selectedRow?.category || "");
  const [description, setDescription] = useState(
    selectedRow?.description || ""
  );
  const [availabilityStatus, setAvailabilityStatus] = useState(
    selectedRow?.availabilityStatus || ""
  );

  const handleEditBuilding = async () => {
  //     try {

  //       const editedBuilding = {
  //         build_id: selectedRow?.build_id,
  //         build_name: name,
  //         build_status: status,
  //       };

  //       const response = await editBuilding(editedBuilding);

  //       if (response && response.status === "success") {
  //         toast.success(t("buildings.validation.edit_msg"));
  //         updateListingData(editedBuilding);
  //         toggle();
  //       } else {
  //         toast.error(t("buildings.validation.err_edit"));
  //       }
  //     } catch (error) {
  //       if (error.name === "ValidationError") {
  //         const errors = {};
  //         error.inner.forEach((e) => {
  //           errors[e.path] = e.message;
  //         });
  //         setValidationErrors(errors);
  //       }
  //     }
    };

  const handleAddProduct = async () => {
    try {
      const product = {
        name: name,
        price: price,
        category: category,
        description: description,
        availabilityStatus: availabilityStatus,
      };

      const response = await createProduct(product);

      if (response && response.status === "success") {
        toast.success("Adding Product Succussfly");

        setName("");
        setPrice("");
        setCategory("");
        setDescription("");
        setAvailabilityStatus("");

        product.product_id = response.message.build_id;

        const _products = [...products];
        _products.unshift(product);
        setProducts(_products);

        toggle();
      } else {
        toast.error("Error adding");
      }
    } catch (error) {
      toast.error(error);
    }
  };
console.log("AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA");
const handleAction = selectedRow ? handleEditBuilding : handleAddProduct;
const buttonLabel = selectedRow
  ? "Edit Product"
  : "Add Product";





  return( 
    <Dialog open={isOpen} onClose={toggle} fullWidth>
    <DialogTitle>{buttonLabel}</DialogTitle>
    
    <DialogContent>
      <Box component="form" noValidate autoComplete="off">
        <FormControl fullWidth margin="normal" >
          <Typography variant="subtitle1">{"name"}</Typography>
          <TextField
            variant="outlined"
            id="name"
            placeholder={t("buildings.add_edit.build_name")}
            value={name}
            onChange={(e) => setName(e.target.value)}
            // helperText={!name && validationErrors.build_name}
          />
        </FormControl>

        <FormControl fullWidth margin="normal" >
          <Typography variant="subtitle1">{"price"}</Typography>
          <Select
            variant="outlined"
            value={price}
            onChange={(e) => setStatus(e.target.value)}
          >
            {statusArray.map((item, index) => (
              <MenuItem key={`building-status-${index}`} value={item}>
                {t(`table.${item}`)}
              </MenuItem>
            ))}
          </Select>
          <FormHelperText>{"!status && validationErrors.status"}</FormHelperText>
        </FormControl>
      </Box>
    </DialogContent>
    
    <DialogActions>
      <Button variant="contained" color="primary" onClick={handleAction}>
        {buttonLabel}
      </Button>
      <Button variant="contained" color="secondary" onClick={toggle}>
        {t("buildings.add_edit.cancel_btn")}
      </Button>
    </DialogActions>
  </Dialog>
    );
};

export default ModalWrapper;
